INSERT INTO "pitanje" VALUES(9,'Šta koristi više vode?','A: Pranje sudova ručno','B: Pranje sudova mašinski',NULL,NULL,1,'Mašinsko pranje sudova koristi za 1/6 manje vode od ručnog pranja. Ponekad je tako lako uštedeti vodu.',2);
INSERT INTO "pitanje" VALUES(10,'Koliko vode može u prosečnom domaćinstvu da se potroši za sat vremena ?','A: 3000 litara','B: 1000 litara','C:5000 litara','D: 350 litara',1,'Većina domaćinstava se vodom snabdeva preko vodomera od ¾“ koji imaju protok 3000 l/h. Nepažnjamom računi veoma brzo mogu da postnu ogromni .',4);